function onCondition() {
	var payrollFields = ['date_final_check_is_needed', 'eligible_for_rehire', 'if_car_allowance_enter_car_allowance_amount','last_known_personal_phone_number', 'last_known_mailing_address', 'final_hours_worked_o_t', 'type_of_separation', 'final_check_given_to_employee_per_that_specific_state_s_regulations', 'last_4_numbers_of_ssn', 'send_check_to', 'final_hours_vacation', 'employee_type', 'voluntary', 'final_hours_severance', 'car_allowance_amount_to_be_paid_is_confirmed_if_applicable', 'final_hours_worked_d_t', 'final_check_given_to_employee_per_that_specific_state_s_regulations', 'last_4_numbers_of_ssn', 'send_check_to','final_hours_worked_clock','final_expense_report_is_received_reviewed_and_approved_if_applicable','benefits_contact_form_provided_to_non_union_employees','what_time_is_payoff_needed','is_same_day_payoff_required','personal_email_address'];
	var userID = g_user.userID;
	//Specialty Services
	var gr = new GlideRecord('sys_user_grmember');
	gr.addQuery('user', userID);
	gr.addQuery('group', '0de9f4781bcf7414df31da49cc4bcbe6');//AG_SegmentSpecialtyServicesPayroll
	gr.query();

	var gr1 = new GlideRecord('sys_user_grmember');
	gr1.addQuery('user', userID);
	gr1.addQuery('group', '58a0d63b1bfa2414df31da49cc4bcb2a');//AG_Segment-SafetyServices-Payroll
	gr1.query();

	var gr2 = new GlideRecord('sys_user_grmember');
	gr2.addQuery('user', userID);
	gr2.addQuery('group', 'ba0aca2e1bb4e810df31da49cc4bcba7');//AG_PayrollAdministrationSupportServices
	gr2.query();

	if(!gr.hasNext() && !gr1.hasNext() && !gr2.hasNext()){
		for(i=0;i<payrollFields.length;i++){
			g_form.setVisible(payrollFields[i],false);
		}
		g_form.setSectionDisplay('payroll', false);
	}
	else{
		for(i=0;i<payrollFields.length;i++){
			g_form.setVisible(payrollFields[i],true);
		}
		g_form.setSectionDisplay('payroll', true);
	}
}

